def parser_handler(args):
    print("HEY 2")


def add_subparser(parser, parents):
    help = "Build documentation to publish"
    parser = parser.add_parser("document",
                                parents=parents,
                                help=help,
                                description=help)

    parser.add_argument(
        "--do-not-open",
        help="Do not automatically open via the default web browser.",
        action="store_true"
    )

    parser.set_defaults(func=parser_handler)
